changeSet.sql:
create table management_company_in_town (
    management_company_id       BIGINT              NOT NULL REFERENCES management_company (id),
    town_id                     BIGINT              NOT NULL REFERENCES town (id)
);

CREATE INDEX management_company_in_town___management_company_id ON management_company_in_town (management_company_id);
CREATE INDEX management_company_in_town___town_id ON management_company_in_town (town_id);
